mapper.select:
select count(1) from  
WHERE
deviceId = #{param.deviceId}

    and joinUserId = #{param.joinUserId}


   and status = #{param.status}

update

 set status = #{status} , lastUpdateTime = #{lastUpdateTime}
WHERE joinUserId = #{joinUserId} and deviceId = #{deviceId}

mapper.delete:
