SELECT dp.abilityId, dma.definedName modelAbilityName, dp.typeName abilityParamsName, dp.sort, dp.value FROM t_device_params dp
LEFT JOIN t_device_model_ability dma
ON dp.abilityId = dma.abilityId AND dma.modelId = #{modelId}
WHERE dp.deviceId = #{deviceId}
AND dp.status = 1

