mapper.select:
select * from


    1 = 1

        AND name like CONCAT('%',CONCAT(#{param.name},'%'))


        AND type = #{param.type}


        AND owerType = #{param.owerType}


        AND status = #{param.status}


        AND ( customerIds like CONCAT(#{param.customerIds},',%')
        or customerIds like CONCAT('%,',#{param.customerIds})
        or customerIds like CONCAT('%,',CONCAT(#{param.customerIds},',%'))
        or customerIds = #{param.customerIds}
        )


limit #{offset},#{limit}

select * from
f, wx_config c
1 = 1 AND ( c.htmlTypeIds LIKE CONCAT( f.id, ',%' ) OR c.htmlTypeIds LIKE CONCAT( '%,', f.id ) OR c.htmlTypeIds LIKE CONCAT( '%,', CONCAT( f.id, ',%' ) ) OR c.htmlTypeIds = f.id )
-- AND ( f.customerIds like CONCAT(c.customerId,',%') -- or f.customerIds like CONCAT('%,',c.customerId) -- or f.customerIds like CONCAT('%,',CONCAT(c.customerId,',%')) -- or f.customerIds = c.customerId -- or f.customerIds is null -- )
AND ( f.typeIds like CONCAT(#{typeIds},',%') or f.typeIds like CONCAT('%,',#{typeIds}) or f.typeIds like CONCAT('%,',CONCAT(#{typeIds},',%')) or f.typeIds = #{typeIds} )
AND c.customerId = #{customerIds}
AND f.type = #{type}
AND f.owerType = #{owerType}
AND f.status = 1
