mapper.select:
SELECT pji.id, pji.name, pji.description, pji.linkType, pji.isRule, pji.ruleId, pji.sourceType, pji.flowStatus, pji.createTime, pji.updateTime, u.nickName createName,u1.nickName updateName
FROM t_project_job_info pji left join t_user u on pji.createUser = u.id left join t_user u1 on  pji.updateUser = u1.id
WHERE 1=1

AND pji.customerId = #{projectPlanInfo.customerId}
AND find_in_set(#{userId}, pji.enableUsers)
LIMIT #{start},#{limit}

